            _fileMock.Setup(x => x.Exists(imageFile1.FullName)).Returns(true);

            var images = new List<ISpriteImage>
            {
                new FileSpriteImage(imageFile1),
                new ByteArraySpriteImage("b", imageByteArray2)
            };

            // Act
